import { call, put, takeLatest } from "redux-saga/effects";

import { getUserDetailsAPI } from "./actions";
import {
  LOAD_USER_API,
  USER_RESPONSE_RECEIVED,
} from "./constants";

import {
  ASYNC_FETCH,
  ASYNC_FETCH_SUCCESS,
  PAGE_LOAD_API_ERROR,
} from "./constants";

function* getApiData(action) {
  yield put({ type: ASYNC_FETCH });
  try {
    debugger;
    const apiResponse = yield call(getUserDetailsAPI, action.orderId);
    const userInfo = apiResponse.data.data;
    const data = { userInfo };

    console.log("getApiData---apiResponse", data);
    if (apiResponse) {
      yield put({ type: USER_RESPONSE_RECEIVED, response: apiResponse.data.data });
    } else {
      console.log("getApiData--- Error", apiResponse);
      yield put({ type: PAGE_LOAD_API_ERROR });
    }
  } catch (e) {
    console.log(e);
    yield put({ type: PAGE_LOAD_API_ERROR });
  }
  yield put({ type: ASYNC_FETCH_SUCCESS });
}

export default function* mySaga() {
  yield takeLatest(LOAD_USER_API, getApiData);
}
